import React from 'react';
import ReactDOM from 'react-dom';
import { Card } from 'react-bootstrap';
import  './short_des.css';


class ShortDesc extends React.Component{

  render(){
    return(
      <Card className="cardcontact">
        <Card.Body>
          <Card.Title>{this.props.item.title}</Card.Title>
          <Card.Text>
            <p><i class={this.props.item.contact.icon}></i>{this.props.item.contact.title}</p>
            <p id="place" class="mx-auto w-50">{this.props.item.text.title}</p>
            <p><i class={this.props.item.contact.icon}></i>{this.props.item.contact.title}</p>
            <p><i class={this.props.item.contact.icon}></i>{this.props.item.contact.title}</p>
            <p><i class={this.props.item.contact.icon}></i>{this.props.item.contact.title}</p>
            <p>{this.props.item.contact.title}</p>
            <p className="social-icons justify-content-center">
                <a href={this.props.item.socialmedia.herf}><i class={this.props.item.socialmedia.icon}></i></a>
                <a href={this.props.item.socialmedia.herf}><i class={this.props.item.tsocialmedia.icon}></i></a>
                <a href={this.props.item.socialmedia.herf}><i class={this.props.item.socialmedia.icon}></i></a>
                <a href={this.props.item.socialmedia.herf}><i class={this.props.item.socialmedia.icon}></i></a>
                <img className={this.props.item.img.class} src={this.props.item.img.src} alt={this.props.item.img.alt} />
            </p>
          </Card.Text>
        </Card.Body>
      </Card>
    )
  }
}

export default ShortDesc;
