import { FRAME_EVENT, FRAME_EVENT_ORIGINS } from '../types';

export enum RETRO_FRAME_COMMANDS {
	SET_SIZE = 'retro:setSize',
	LOADING_DONE = 'retro:loadingDone'
}

export interface RETRO_FRAME_EVENT extends FRAME_EVENT {
	command: RETRO_FRAME_COMMANDS;
	origin: FRAME_EVENT_ORIGINS.RETRO | FRAME_EVENT_ORIGINS.MOCK_RETRO;
	visible: boolean;
	id: string;
}

export interface SET_SIZE_RETRO_FRAME_EVENT extends RETRO_FRAME_EVENT {
	value: number;
}
